def astar(graph, start, goal, h):
    pq = [(h(start), 0, start, [start])]
    visited = set()
    while pq:
        _, g, node, path = heapq.heappop(pq)
        if node == goal: return path
        if node in visited: continue
        visited.add(node)
        for neighbor, cost in graph[node]:
